$(function(){$("div.hover-actions a.change-status").click(function(){changeStatus($(this));return false});$("div.hover-actions a.delete").click(function(){deleteComment($(this),$(this).attr("href"));return false});$("div.hover-actions a.reply-link").click(function(){showReplyContainer($(this));return false});$("#reply-cancel-link").click(function(){$("#reply-container").fadeOut()});$("#replytocommentform").submit(function(){if($("#replyContent").val()==""){growl("Please fill the reply text before submit!",true,true);return false}return true})});function changeStatus(A){$.post($(A).attr("href"),{},function(B){if(B.indexOf("<!--msg-")>-1){showMessage(B)}else{$(A).parents("tr").eq(0).replaceWith(B);growl("Comment status updated!",false,false)}})}function deleteComment(B,A){if(!confirm("Do you really want to delete the selected comment?")){return false}$.post(A,{},function(C){if(C.indexOf("<!--msg-alert")>-1){showMessage(C)}else{$(B).parents("tr").eq(0).remove();showMessage(C)}})}function showReplyContainer(A){$(A).parents("tr").eq(0).after($("#reply-container"));$("#reply-content").val("");$("#replyToCommentId").val($(A).attr("rel"));$("#reply-container").fadeIn()};